    }

    return (
        <Card style={{ backgroundColor: 'black'}}>
            <Card.Title>
                <h2>{bookDetail[0].title} - {bookDetail[0].author}</h2>
            </Card.Title>
            <Card.Body>
                <div className="bookText">Publication date: {bookDetail[0].publicationDate}</div>
                <div className="bookText">Genre: {bookDetail[0].genre}</div>
                <div className="bookText">Description: {bookDetail[0].description}</div>
            </Card.Body>
        </Card>
    );
};

export default BookDetail;
