import { createContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import { MyContext } from '../App';
import BookCard from './BookCard';
import './BookItem.css';

const BookItem = ({title, author, publicationDate, genre}) => {

    const context = createContext(MyContext);
    const navigate = useNavigate();
    const {id} = useParams();
    console.log(context[0]);
    const routeDetail = () => {
        navigate(`detail/${id}`);
    };

    return (
        <MyContext.Consumer>
            <li onClick={routeDetail}>
            <BookCard className="bookItem">
                <div>
                    <h2>{title} - {author}</h2>
                    <div className="bookText">Publication date: {publicationDate}</div>
                    <div className="bookText">Genre: {genre}</div>
                </div>
            </BookCard>
        </li>
        </MyContext.Consumer>
    )
}

export default BookItem;
