import { useContext, useState } from "react";

import { MyContext } from "../App"; 
import BookList from "./BookList";
import BookCard from "./BookCard";
import './Books.css';

const Books = () => {

    const context = useContext(MyContext);

    const [userInput, setUserInput] = useState('');
    const [searchResult, setSearchResult] = useState(null);

    const inputHandler = (e) => {
        e.preventDefault();
        setUserInput(e.target.value);
      };

    const result = context.filter((book) => book.title.toLowerCase() === userInput || book.author.toLowerCase() === userInput);
    if(result.length > 0){
        return <BookCard className="books">
        <input type="text" className="search" onChange={inputHandler}/>
        <BookList books={result}/>
    </BookCard>
    }
    else{
        return (

            <BookCard className="books">
                <input type="text" className="search" onChange={inputHandler}/>
                <BookList books={context}/>
            </BookCard>
        );
    }
};

export default Books;
