import { useContext, useState } from "react";

import { MyContext } from "../App"; 
import BookList from "./BookList";
import BookCard from "./BookCard";
import './Books.css';

const Books = () => {

    var context = useContext(MyContext);
    const [userInput, setUserInput] = useState('');

    const inputHandler = (e) =>{
      let lowerCase = e.target.value.toLowerCase();
      setUserInput(lowerCase);
      var searchResult = context.filter((book) => {book.title.toLowerCase() === lowerCase || book.author.toLowerCase() === lowerCase});
      if(searchResult != null){
          context = searchResult;
      }
    };

    return (

        <BookCard className="books">
            <input type="text" className="search" onChange={inputHandler}/>
            <BookList books={context}/>
        </BookCard>
    );
};

export default Books;
