import { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import BookCard from './BookCard';
import './BookItem.css';

const BookItem = ({value}) => {

    const [data] = useState(JSON.parse(value));
    console.log(data);
    const navigate = useNavigate();
    const {id, title, author, publicationDate, genre, description} = useParams(data);

    console.log(value[0].title); 

    const routeDetail = () => {
        navigate(`detail/${id}`);
    };

    return (
        <li onClick={routeDetail}>
            <BookCard className="bookItem">
                <div>
                    <h2>{title} - {author}</h2>
                    <div className="bookText">Publication date: {publicationDate}</div>
                    <div className="bookText">Genre: {genre}</div>
                    <div className="bookText">Description: {description}</div>
                </div>
            </BookCard>
        </li>
    )
}

export default BookItem;
