import { useContext, useState } from "react";

import { MyContext } from "../App"; 
import BookList from "./BookList";
import BookCard from "./BookCard";
import './Books.css';

const Books = () => {

    const context = useContext(MyContext);

    const [userInput, setUserInput] = useState('');
    var searchResult = [];

    const inputHandler = (e) => {
        e.preventDefault();
        setUserInput(e.target.value);
      };

    searchResult = context.filter((book) => book.title.toLowerCase().includes(userInput) || book.author.toLowerCase().match(userInput));
    if(searchResult.length !== 0 && userInput != ""){
        return <BookCard className="bookItem">
        <div>
            <h2>{searchResult.title} - {searchResult.author}</h2>
            <div className="bookText">Publication date: {searchResult.publicationDate}</div>
            <div className="bookText">Genre: {searchResult.genre}</div>
        </div>
    </BookCard>
    }
    else{
        return (  
            <BookCard className="books">
                <input type="text" className="search" value={userInput} onChange={inputHandler}/>
                <BookList books={context}/>
            </BookCard>
        );
    }
};

export default Books;
