import { useContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { MyContext } from '../App';

import BookCard from './BookCard';
import './BookItem.css';

const BookItem = ({title, author, publicationDate, genre}) => {

    const context = useContext(MyContext);
    const navigate = useNavigate();
    const id = useParams();
    console.log(id);
    const routeDetail = () => {
        navigate(`detail/${id}`);
    };

    return (
        <li onClick={routeDetail}>
            <BookCard className="bookItem">
                <div>
                    <h2>{context.title} - {context.author}</h2>
                    <div className="bookText">Publication date: {publicationDate}</div>
                    <div className="bookText">Genre: {genre}</div>
                </div>
            </BookCard>
        </li>
    )
}

export default BookItem;
