import BookCard from './BookCard';
import './BookItem.css';

const BookItem = (props) => {
    const navigate = useNavigate;
    return (
        <li onClick={navigate('bookDetail')}>
            <BookCard className="bookItem">
                <div>
                    <h2>{props.title} - {props.author}</h2>
                    <div className="bookText">Publication date: {props.publicationDate}</div>
                    <div className="bookText">Genre: {props.genre}</div>
                </div>
            </BookCard>
        </li>
    )
}

export default BookItem;
