import { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import BookCard from './BookCard';
import './BookItem.css';

const BookItem = ({value}) => {

    const [data, setData] = useState;
    const navigate = useNavigate();
    const {id} = useParams(value);
    console.log(value[0].title); 

    const routeDetail = () => {
        navigate(`detail/${id}`);
    };

    return (
        <li onClick={routeDetail}>
            <BookCard className="bookItem">
                <div>
                    <h2>{value[0].title} - {value[0].author}</h2>
                    <div className="bookText">Publication date: {value[0].publicationDate}</div>
                    <div className="bookText">Genre: {value[0].genre}</div>
                </div>
            </BookCard>
        </li>
    )
}

export default BookItem;
