import { useNavigate, useParams } from 'react-router-dom';

import BookCard from './BookCard';
import './BookItem.css';

const BookItem = ({book}) => {

    const navigate = useNavigate();
    const {id} = useParams();

    const routeDetail = () => {
        navigate(`detail/${id}`);
    };

    return (
        <li onClick={routeDetail}>
            <BookCard className="bookItem">
                <div>
                    <h2>{book.title} - {book.author}</h2>
                    <div className="bookText">Publication date: {book.publicationDate}</div>
                    <div className="bookText">Genre: {book.genre}</div>
                    <div className="bookText">Description: {book.description}</div>
                </div>
            </BookCard>
        </li>
    )
}

export default BookItem;
