import { useContext, useState } from 'react';

import { MyContext } from '../App';
import Books from '../components/Books';
import BookCard from '../components/BookCard';
import './BooksPage.css'

const BooksPage = () => {
    const context = useContext(MyContext);

    const [userInput, setUserInput] = useState('');
    var searchResult = [];

    const inputHandler = (e) => {
        e.preventDefault();
        setUserInput(e.target.value);
    };

    searchResult = context.filter((book) => book.title.toLowerCase().includes(userInput.toLowerCase()) || book.author.toLowerCase().includes(userInput.toLowerCase()));
    console.log(searchResult);
    if (searchResult.length !== 0 && userInput != "") {
        const searchResultFilter = searchResult.map(book => {
            return <div>
            <h1 className='title'>List of books. Click on a book to view additional information.</h1>
            <input type="text" className="search" value={userInput} onChange={inputHandler} />
            <Books books={searchResultFilter}/>
          </div>
        });
        return searchResultFilter;
    }
    else{
      return (
        <div>
          <h1 className='title'>List of books. Click on a book to view additional information.</h1>
          <input type="text" className="search" value={userInput} onChange={inputHandler} />
          <Books books={context}/>
        </div>
      );
    }
    return (
      <div>
        <h1 className='title'>List of books. Click on a book to view additional information.</h1>
        <input type="text" className="search" value={userInput} onChange={inputHandler} />
        <Books books={context}/>
      </div>
    );
};

export default BooksPage;
