import { useContext } from "react";
import { useParams } from "react-router-dom";
import { MyContext } from "../App";
import BookCard from "./BookCard";

const BookDetail = ({book}) => {

    const context = useContext(MyContext);
    const { id } = useParams();
    console.log(context);

    const bookDetail = context.filter(book => {
        return book.id === id;
    });

    return (
        <BookCard className="bookItem">
            <div>
                <h2>{bookDetail.title} - {bookDetail.author}</h2>
                <div className="bookText">Publication date: {bookDetail.publicationDate}</div>
                <div className="bookText">Genre: {bookDetail.genre}</div>
                <div className="bookText">Description: {bookDetail.description}</div>
            </div>
        </BookCard>
    );
};

export default BookDetail;
