import { useContext, useState } from "react";

import { MyContext } from "../App"; 
import BookList from "./BookList";
import BookCard from "./BookCard";
import './Books.css';

const Books = () => {

    const context = useContext(MyContext);

    const [userInput, setUserInput] = useState('');
    var searchResult = [];

    const inputHandler = (e) => {
        e.preventDefault();
        setUserInput(e.target.value);
      };

    searchResult = context.filter((book) => book.title.toLowerCase().includes(userInput.toLowerCase()) || book.author.toLowerCase().includes(userInput.toLowerCase()));
    if(searchResult.length !== 0 && userInput != ""){
        return(  
            <BookCard className="books">
                <input type="text" className="search" value={userInput} onChange={inputHandler}/>
                <li onClick={routeDetail}>
            <BookCard className="bookItem">
                <div>
                    <h2>{book.title} - {book.author}</h2>
                    <div className="bookText">Publication date: {book.publicationDate}</div>
                    <div className="bookText">Genre: {book.genre}</div>
                </div>
            </BookCard>
        </li>
            </BookCard>
        );
    }
    else{
        return (  
            <BookCard className="books">
                <input type="text" className="search" value={userInput} onChange={inputHandler}/>
                <BookList books={context}/>
            </BookCard>
        );
    }
};

export default Books;
